        @self.app.route('/api/company-login-redirect', methods=['POST'])
        def company_login_redirect():
            """Ana sayfadan ≈üirket giri≈ü y√∂nlendirme"""
            try:
                company_id = request.form.get('company_id', '').strip()
                
                if not company_id:
                    return '''
                    <script>
                        alert("‚ùå ≈ûirket ID bo≈ü bƒ±rakƒ±lamaz!");
                        window.history.back();
                    </script>
                    '''
                
                # ≈ûirket ID formatƒ±nƒ± kontrol et (Normal + Demo)
                if not (company_id.startswith('COMP_') or company_id.startswith('demo_')):
                    return '''
                    <script>
                        alert("‚ùå Ge√ßersiz ≈ûirket ID formatƒ±!\\n≈ûirket ID'niz COMP_ veya demo_ ile ba≈ülamalƒ±dƒ±r.");
                        window.history.back();
                    </script>
                    '''
                
                # ≈ûirket var mƒ± kontrol et - Safe query with fallback
                conn = self.db.get_connection()
                cursor = conn.cursor()
                placeholder = self.db.get_placeholder() if hasattr(self.db, 'get_placeholder') else '?'
                
                # Try with account_type column first, fallback if column doesn't exist
                try:
                    cursor.execute(f'SELECT company_name, account_type, demo_expires_at FROM companies WHERE company_id = {placeholder}', (company_id,))
                    company = cursor.fetchone()
                except Exception as e:
                    if 'account_type' in str(e) and 'does not exist' in str(e):
                        # Column doesn't exist, use fallback query
                        logger.warning(f"‚ö†Ô∏è account_type column missing, using fallback query")
                        cursor.execute(f'SELECT company_name FROM companies WHERE company_id = {placeholder}', (company_id,))
                        result = cursor.fetchone()
                        if result:
                            company = (result[0], 'full', None)  # Default values
                        else:
                            company = None
                    else:
                        raise e
                conn.close()
                
                if not company:
                    return f'''
                    <script>
                        alert("‚ùå Company with ID '{company_id}' not found!\\nPlease check your company ID.");
                        window.history.back();
                    </script>
                    '''
                
                # Demo hesap kontrol√º
                company_name, account_type, demo_expires_at = company
                
                if account_type == 'demo' and demo_expires_at:
                    # Demo s√ºresi kontrol√º
                    if isinstance(demo_expires_at, str):
                        expire_date = datetime.fromisoformat(demo_expires_at.replace('Z', '+00:00'))
                    else:
                        expire_date = demo_expires_at
                    
                    if datetime.now() > expire_date:
                        return f'''
                        <script>
                            alert("‚ùå Demo hesabƒ±nƒ±zƒ±n s√ºresi dolmu≈ü!\\nDemo s√ºresi: 7 g√ºn\\n≈ûirket: {company_name}\\n\\nYeni demo hesap olu≈üturmak i√ßin ana sayfaya d√∂n√ºn.");
                            window.location.href = '/';
                        </script>
                        '''
                    
                    # Demo bilgisi g√∂ster
                    return f'''
                    <script>
                        alert("üéØ Demo Hesap Tespit Edildi!\\n\\n≈ûirket: {company_name}\\nDemo S√ºresi: 7 g√ºn\\nKalan S√ºre: {(expire_date - datetime.now()).days} g√ºn\\n\\nGiri≈ü yapƒ±lƒ±yor...");
                        window.location.href = '/company/{company_id}/login';
                    </script>
                    '''
                
                # Normal ≈üirket giri≈ü sayfasƒ±na y√∂nlendir
                return redirect(f'/company/{company_id}/login')
                
            except Exception as e:
                logger.error(f"‚ùå ≈ûirket giri≈ü y√∂nlendirme hatasƒ±: {e}")
                return f'''
                <script>
                    alert("‚ùå Bir hata olu≈ütu: {str(e)}");
                    window.history.back();
                </script>
                '''
        
