        # ≈ûirket istatistikleri API (Enhanced)
        @self.app.route('/api/company/<company_id>/stats', methods=['GET'])
        def get_company_stats(company_id):
            """Unified ≈üirket istatistikleri - Database'den ger√ßek kamera sayƒ±sƒ±"""
            user_data = self.validate_session()
            if not user_data or user_data['company_id'] != company_id:
                return jsonify({'error': 'Yetkisiz eri≈üim'}), 401
            
            try:
                # MultiTenant database'den base istatistikleri al
                stats = self.db.get_company_stats(company_id)
                
                # Ger√ßek kamera sayƒ±sƒ±nƒ± database'den al (unified approach)
                try:
                    cameras = self.db.get_company_cameras(company_id)
                    total_cameras = len(cameras)
                    active_cameras = len([c for c in cameras if c.get('status') == 'active'])
                    discovered_cameras = len([c for c in cameras if c.get('status') == 'discovered'])
                    
                    # Kamera istatistiklerini g√ºncelle
                    stats.update({
                        'active_cameras': active_cameras,
                        'total_cameras': total_cameras,
                        'discovered_cameras': discovered_cameras,
                        'inactive_cameras': total_cameras - active_cameras
                    })
                    
                    logger.info(f"‚úÖ Unified stats for company {company_id}: {total_cameras} cameras ({active_cameras} active, {discovered_cameras} discovered)")
                    
                except Exception as camera_error:
                    logger.error(f"‚ùå Error getting camera stats: {camera_error}")
                    # Fallback to existing stats without camera updates
                
                # Enhanced stats with unified camera data
                enhanced_stats = {
                    'active_cameras': stats.get('active_cameras', 0),
                    'total_cameras': stats.get('total_cameras', 0),
                    'discovered_cameras': stats.get('discovered_cameras', 0),
                    'compliance_rate': stats.get('compliance_rate', 0),
                    'today_violations': stats.get('today_violations', 0),
                    'active_workers': stats.get('active_workers', 0),
                    'total_detections': stats.get('total_detections', 0),
                    'monthly_violations': stats.get('monthly_violations', 0),
                    
                    # Trend indicators - Backward compatibility
                    'cameras_trend': stats.get('cameras_trend', 0),
                    'compliance_trend': stats.get('compliance_trend', 0),
                    'violations_trend': stats.get('violations_trend', 0),
                    'workers_trend': stats.get('workers_trend', 0),
                    'people_trend': stats.get('people_trend', 0),
                    'fps_trend': stats.get('fps_trend', 0),
                    'processing_trend': stats.get('processing_trend', 0),
                    
                    # Unified data source indicator
                    'data_source': 'unified_database',
                    'last_updated': datetime.now().isoformat()
                }
                
                return jsonify(enhanced_stats)
                
            except Exception as e:
                logger.error(f"‚ùå Stats error for company {company_id}: {e}")
                return jsonify({
                    'error': 'ƒ∞statistikler getirilemedi',
                    'details': str(e)
                }), 500
        
        # ≈ûirket kameralarƒ± API
        @self.app.route('/api/company/<company_id>/cameras', methods=['GET'])
        def get_company_cameras(company_id):
            """≈ûirket kameralarƒ±nƒ± getir - Unified Database Source"""
            user_data = self.validate_session()
            if not user_data or user_data['company_id'] != company_id:
                return jsonify({'error': 'Yetkisiz eri≈üim'}), 401
            
            try:
                # Unified approach: Database'den kameralarƒ± al
                cameras = self.db.get_company_cameras(company_id)
                
                # Enterprise camera manager entegrasyonu
                if hasattr(self, 'camera_manager') and self.camera_manager:
                    # Real-time status update
                    for camera in cameras:
                        try:
                            # IP'den camera manager'da status kontrol et
                            if camera.get('ip_address'):
                                status_info = self._get_realtime_camera_status(camera['ip_address'])
                                if status_info:
                                    camera.update(status_info)
                        except Exception as e:
                            logger.debug(f"Real-time status check failed for {camera.get('name', 'unknown')}: {e}")
                
                # Kamera sayƒ±sƒ± ve summary bilgileri ekle
                total_cameras = len(cameras)
                active_cameras = len([c for c in cameras if c.get('status') == 'active'])
                
                result = {
                    'success': True, 
                    'cameras': cameras,
                    'total': total_cameras,
                    'active': active_cameras,
                    'summary': {
                        'total_cameras': total_cameras,
                        'active_cameras': active_cameras,
                        'inactive_cameras': total_cameras - active_cameras,
                        'last_updated': datetime.now().isoformat()
                    }
                }
                
                logger.info(f"‚úÖ Retrieved {total_cameras} cameras for company {company_id}")
                return jsonify(result)
                
            except Exception as e:
                logger.error(f"‚ùå Error getting cameras for company {company_id}: {e}")
                return jsonify({'success': False, 'error': 'Kameralar getirilemedi'}), 500
        
        # Kamera ekleme API
        @self.app.route('/api/company/<company_id>/cameras', methods=['POST'])
        def add_camera(company_id):
            """Yeni kamera ekleme"""
            try:
                logger.info(f"üöÄ ADD CAMERA REQUEST STARTED")
                logger.info(f"üìã Company ID: {company_id}")
                logger.info(f"üì° Request method: {request.method}")
                logger.info(f"üì° Request headers: {dict(request.headers)}")
                logger.info(f"üì° Request data: {request.get_data()}")
                
                # Session kontrol√º
                session_id = request.cookies.get('session_id')
                logger.info(f"üç™ Session ID from cookie: {session_id}")
                
                user_data = self.validate_session()
                logger.info(f"üë§ User validation result: {user_data is not None}")
                if user_data:
                    logger.info(f"üë§ User data: {user_data}")
                
                if not user_data or user_data['company_id'] != company_id:
                    logger.error(f"‚ùå Unauthorized access attempt")
                    logger.error(f"‚ùå User data: {user_data}")
                    logger.error(f"‚ùå Expected company_id: {company_id}")
                    return jsonify({'error': 'Yetkisiz eri≈üim'}), 401
                
                logger.info(f"‚úÖ User authorized successfully")
                
                # Abonelik limit kontrol√º
                logger.info(f"üîç Checking subscription limits...")
                subscription_info = self.get_subscription_info_internal(company_id)
                logger.info(f"üìä Subscription info: {subscription_info}")
                    
                if not subscription_info['success']:
                    logger.error(f"‚ùå Subscription info failed: {subscription_info}")
                    return jsonify({'success': False, 'error': 'Abonelik bilgileri alƒ±namadƒ±'}), 400
                
                # subscription_info doƒürudan t√ºm bilgileri i√ßeriyor, 'subscription' key'i yok
                current_cameras = subscription_info['used_cameras']
                max_cameras = subscription_info['max_cameras']
                
                logger.info(f"üìà Camera limits - Current: {current_cameras}, Max: {max_cameras}")
                
                # Limit kontrol√º
                if current_cameras >= max_cameras:
                    logger.warning(f"‚ö†Ô∏è Camera limit reached: {current_cameras}/{max_cameras}")
                    return jsonify({
                        'success': False, 
                        'error': f'Kamera limiti a≈üƒ±ldƒ±! Mevcut: {current_cameras}/{max_cameras}',
                        'limit_reached': True,
                        'current_cameras': current_cameras,
                        'max_cameras': max_cameras,
                        'subscription_type': subscription_info['subscription_type']
                    }), 403
                
                logger.info(f"‚úÖ Camera limit check passed")
                
                data = request.json
                logger.info(f"üìπ Raw camera data received: {data}")
                
                # Veri doƒürulama - Field mapping d√ºzeltmesi
                # Frontend'den gelen field isimleri: camera_name, camera_location, camera_ip, camera_port, camera_protocol, camera_path
                # Backend'in beklediƒüi field isimleri: name, location, ip_address, port, protocol, stream_path
                
                # Field mapping yap
                mapped_data = {
                    'name': data.get('camera_name'),
                    'location': data.get('camera_location'),
                    'ip_address': data.get('camera_ip'),
                    'port': data.get('camera_port', 8080),
                    'protocol': data.get('camera_protocol', 'http'),
                    'stream_path': data.get('camera_path', '/video'),
                    'username': data.get('camera_username', ''),
                    'password': data.get('camera_password', '')
                }
                
                # Required fields kontrol√º
                required_fields = ['name', 'location', 'ip_address']
                missing_fields = [field for field in required_fields if not mapped_data.get(field)]
                
                if missing_fields:
                    logger.error(f"‚ùå Missing required fields: {missing_fields}")
                    return jsonify({'success': False, 'error': f'Eksik alanlar: {", ".join(missing_fields)}'}), 400
                
                logger.info(f"‚úÖ Data validation passed")
                logger.info(f"üìù Camera name: {mapped_data.get('name')}")
                logger.info(f"üìç Location: {mapped_data.get('location')}")
                logger.info(f"üåê IP Address: {mapped_data.get('ip_address')}")
                logger.info(f"üîå Port: {mapped_data.get('port')}")
                logger.info(f"üîê Protocol: {mapped_data.get('protocol')}")
                logger.info(f"üìÅ Stream Path: {mapped_data.get('stream_path')}")
                
                # Kamera ekle
                logger.info(f"üíæ Calling database add_camera function...")
                success, result = self.db.add_camera(company_id, mapped_data)
                logger.info(f"üíæ Database result - Success: {success}, Result: {result}")
                
                if success:
                    logger.info(f"‚úÖ Camera added successfully with ID: {result}")
                    return jsonify({'success': True, 'camera_id': result})
                else:
                    logger.error(f"‚ùå Camera addition failed: {result}")
                    return jsonify({'success': False, 'error': result}), 400
                    
            except Exception as e:
                logger.error(f"üí• EXCEPTION in add_camera: {e}")
                logger.error(f"üí• Exception type: {type(e)}")
                import traceback
                logger.error(f"üí• Full traceback: {traceback.format_exc()}")
                return jsonify({'success': False, 'error': 'Kamera eklenemedi'}), 500
        
        # ≈ûirket uyarƒ±larƒ± API - ƒ∞Yƒ∞LE≈ûTƒ∞Rƒ∞LDƒ∞: Yeni alerts tablosu kullanƒ±yor
        @self.app.route('/api/company/<company_id>/alerts', methods=['GET'])
        def get_company_alerts(company_id):
            """≈ûirket uyarƒ±larƒ± - Yeni alerts tablosu ile ger√ßek dinamik veriler"""
            user_data = self.validate_session()
            if not user_data or user_data['company_id'] != company_id:
                return jsonify({'error': 'Yetkisiz eri≈üim'}), 401
            
            try:
                conn = self.db.get_connection()
                cursor = conn.cursor()
                
                placeholder = self.db.get_placeholder() if hasattr(self.db, 'get_placeholder') else '?'
                
                # Son 24 saat i√ßindeki alerts'leri getir
                if hasattr(self.db, 'db_adapter') and self.db.db_adapter.db_type == 'postgresql':
                    time_filter = "a.created_at >= NOW() - INTERVAL '24 hours'"
                else:
                    time_filter = "a.created_at >= datetime('now', '-24 hours')"
                
                cursor.execute(f"""
                    SELECT a.alert_id, a.alert_type, a.severity, a.title, a.message, 
                           a.status, a.created_at, a.camera_id, c.camera_name
                    FROM alerts a
                    LEFT JOIN cameras c ON a.camera_id = c.camera_id
                    WHERE a.company_id = {placeholder} 
                    AND {time_filter}
                    ORDER BY a.created_at DESC
                    LIMIT 20
                """, (company_id,))
                
                alerts_data = cursor.fetchall()
                alerts = []
                
                for alert in alerts_data:
                    # PostgreSQL Row object vs SQLite tuple compatibility
                    if hasattr(alert, 'keys'):  # PostgreSQL Row object
                        alert_data = {
                            'alert_id': alert['alert_id'],
                            'alert_type': alert['alert_type'],
                            'severity': alert['severity'],
                            'title': alert['title'],
                            'message': alert['message'],
                            'status': alert['status'],
                            'created_at': alert['created_at'],
                            'camera_id': alert['camera_id'],
                            'camera_name': alert['camera_name']
                        }
                    else:  # SQLite tuple
                        alert_data = {
                            'alert_id': alert[0],
                            'alert_type': alert[1],
                            'severity': alert[2],
                            'title': alert[3],
                            'message': alert[4],
                            'status': alert[5],
                            'created_at': alert[6],
                            'camera_id': alert[7],
                            'camera_name': alert[8]
                        }
                    
                    # Timestamp'i formatla
                    try:
                        from datetime import datetime
                        if isinstance(alert_data['created_at'], str):
                            dt = datetime.fromisoformat(alert_data['created_at'].replace('Z', '+00:00'))
                        else:
                            dt = alert_data['created_at']
                        time_str = dt.strftime('%H:%M')
                    except:
                        time_str = 'Bilinmiyor'
                    
                    # Alert severity'ye g√∂re icon ve renk belirle
                    severity_config = {
                        'critical': {'icon': 'üî¥', 'color': 'danger'},
                        'warning': {'icon': 'üü°', 'color': 'warning'},
                        'info': {'icon': 'üîµ', 'color': 'info'},
                        'success': {'icon': 'üü¢', 'color': 'success'}
                    }
                    
                    severity_info = severity_config.get(alert_data['severity'].lower(), 
                                                     {'icon': 'üîµ', 'color': 'info'})
                    
                    alerts.append({
                        'alert_id': alert_data['alert_id'],
                        'type': alert_data['alert_type'],
                        'title': alert_data['title'],
                        'message': alert_data['message'],
                        'time': time_str,
                        'camera_name': alert_data['camera_name'] or 'Sistem',
                        'severity': alert_data['severity'],
                        'status': alert_data['status'],
                        'icon': severity_info['icon'],
                        'color': severity_info['color']
                    })
                
                                # Eƒüer ger√ßek veri yoksa demo veriler g√∂ster
                if not alerts:
                    alerts = [
                        {
                            'alert_id': 'demo_1',
                            'type': 'system',
                            'title': 'Sistem Hazƒ±rlanƒ±yor',
                            'message': 'PPE detection sistemi hazƒ±rlanƒ±yor, kameralar test ediliyor',
                            'time': '≈ûimdi',
                            'camera_name': 'Sistem',
                            'severity': 'info',
                            'status': 'active',
                            'icon': 'üîµ',
                            'color': 'info'
                        }
                    ]
                
                conn.close()
                
                return jsonify({
                    'success': True,
                    'alerts': alerts,
                    'total_alerts': len(alerts),
                    'active_alerts': len([a for a in alerts if a['status'] == 'active']),
                    'resolved_alerts': len([a for a in alerts if a['status'] == 'resolved'])
                })
                
            except Exception as e:
                logger.error(f"‚ùå Uyarƒ±lar y√ºklenemedi: {e}")
                return jsonify({
                    'success': False,
                    'error': 'Uyarƒ±lar y√ºklenemedi',
                    'alerts': []
                }), 500
        
