        @self.app.route('/company/<company_id>/settings', methods=['GET'])
        def company_settings(company_id):
            """≈ûirket ayarlarƒ± sayfasƒ±"""
            try:
                logger.info(f"üîç Settings page request: company_id={company_id}")
                
                # Session validation with detailed logging
                session_id = session.get('session_id')
                logger.info(f"üîç Session ID from session: {session_id}")
                
                user_data = self.validate_session()
                logger.info(f"üîç Validated user data: {user_data}")
                
                if not user_data:
                    logger.warning(f"‚ö†Ô∏è No user data found, redirecting to login")
                    return redirect(f'/company/{company_id}/login')
                
                if user_data.get('company_id') != company_id:
                    logger.warning(f"‚ö†Ô∏è Company ID mismatch: session={user_data.get('company_id')}, request={company_id}")
                    return redirect(f'/company/{company_id}/login')
                
                logger.info(f"‚úÖ Session validation successful for settings page")
                
            except Exception as e:
                logger.error(f"‚ùå Settings page error: {e}")
                return redirect(f'/company/{company_id}/login')
            
            # ≈ûirket bilgilerini y√ºkle - Database Adapter kullan
            try:
                if hasattr(self.db, 'get_company_info'):
                    # Database adapter'da get_company_info fonksiyonu varsa kullan
                    company_info = self.db.get_company_info(company_id)
                    if not company_info:
                        company_info = {}
                else:
                    # Fallback: Eski y√∂ntem
                    conn = self.db.get_connection()
                    cursor = conn.cursor()
                    
                    placeholder = self.db.get_placeholder() if hasattr(self.db, 'get_placeholder') else '?'
                    cursor.execute(f'''
                        SELECT company_name, contact_person, email, phone, sector, address, profile_image, logo_url
                        FROM companies WHERE company_id = {placeholder}
                    ''', (company_id,))
                    
                    company_data = cursor.fetchone()
                    conn.close()
                    
                    if company_data:
                        # SQLite Row object handling - try dict access first, fallback to tuple
                        try:
                            company_info = {
                                'company_name': company_data.get('company_name', ''),
                                'contact_person': company_data.get('contact_person', ''),
                                'email': company_data.get('email', ''),
                                'phone': company_data.get('phone', ''),
                                'sector': company_data.get('sector', 'construction'),
                                'address': company_data.get('address', ''),
                                'profile_image': company_data.get('profile_image', ''),
                                'logo_url': company_data.get('logo_url', '')
                            }
                        except AttributeError:
                            # Fallback to tuple access for sqlite3.Row objects
                            company_info = {
                                'company_name': company_data[0] or '',
                                'contact_person': company_data[1] or '',
                                'email': company_data[2] or '',
                                'phone': company_data[3] or '',
                                'sector': company_data[4] or 'construction',
                                'address': company_data[5] or '',
                                'profile_image': company_data[6] or '',
                                'logo_url': company_data[7] if len(company_data) > 7 else ''
                            }
                    else:
                        company_info = {}
                
                # user_data'yƒ± da g√ºncelle
                user_data.update(company_info)
                
            except Exception as e:
                logger.error(f"‚ùå ≈ûirket bilgileri y√ºklenirken hata: {e}")
                # Varsayƒ±lan deƒüerler
                company_info = {
                    'company_name': '',
                    'contact_person': '',
                    'email': '',
                    'phone': '',
                    'sector': 'construction',
                    'address': '',
                    'profile_image': '',
                    'logo_url': ''
                }
                user_data.update(company_info)
            
            return render_template_string(self.get_company_settings_template(), 
                                        company_id=company_id, 
                                        user_data=user_data,
                                        company=company_info)
        
        @self.app.route('/api/company/<company_id>/profile', methods=['PUT'])
        def update_company_profile(company_id):
            """≈ûirket profili g√ºncelle - Bo≈ü alanlarƒ± koruyarak"""
            try:
                # Session kontrol√º
                user_data = self.validate_session()
                if not user_data or user_data.get('company_id') != company_id:
                    return jsonify({'success': False, 'error': 'Ge√ßersiz oturum'}), 401
                
                data = request.get_json()
                if not data:
                    return jsonify({'success': False, 'error': 'Veri gerekli'}), 400
                
                # Debug: Gelen veriyi logla
                print(f"üîç Profile update data: {data}")
                
                # Profil g√ºncelleme
                conn = self.db.get_connection()
                cursor = conn.cursor()
                
                # ≈ûirket bilgilerini g√ºncelle - Sadece dolu alanlarƒ± g√ºncelle
                placeholder = self.db.get_placeholder() if hasattr(self.db, 'get_placeholder') else '?'
                
                # Timestamp fonksiyonu
                if hasattr(self.db, 'db_adapter') and self.db.db_adapter.db_type == 'postgresql':
                    timestamp_func = 'CURRENT_TIMESTAMP'
                else:
                    timestamp_func = 'datetime(\'now\')'
                
                # Sadece dolu alanlarƒ± g√ºncelle - Bo≈ü string'leri koru
                update_fields = []
                update_values = []
                
                if data.get('company_name') and data.get('company_name').strip():
                    update_fields.append(f"company_name = {placeholder}")
                    update_values.append(data.get('company_name').strip())
                
                if data.get('contact_person') and data.get('contact_person').strip():
                    update_fields.append(f"contact_person = {placeholder}")
                    update_values.append(data.get('contact_person').strip())
                
                if data.get('phone') and data.get('phone').strip():
                    update_fields.append(f"phone = {placeholder}")
                    update_values.append(data.get('phone').strip())
                
                if data.get('sector') and data.get('sector').strip():
                    update_fields.append(f"sector = {placeholder}")
                    update_values.append(data.get('sector').strip())
                
                if data.get('address') and data.get('address').strip():
                    update_fields.append(f"address = {placeholder}")
                    update_values.append(data.get('address').strip())
                
                # Email'i sadece dolu ise g√ºncelle
                if data.get('email') and data.get('email').strip():
                    update_fields.append(f"email = {placeholder}")
                    update_values.append(data.get('email').strip())
                
                # Logo URL'i koru - Mevcut logo_url'i al ve g√ºncelle
                print(f"üîç Logo URL koruma ba≈ülatƒ±lƒ±yor...")
                try:
                    # √ñnce logo_url kolonunun var olup olmadƒ±ƒüƒ±nƒ± kontrol et
                    print(f"üîç Logo URL kolonu kontrol ediliyor...")
                    
                    if hasattr(self.db, 'db_adapter') and self.db.db_adapter.db_type == 'postgresql':
                        # PostgreSQL i√ßin
                        try:
                            cursor.execute("""
                                SELECT column_name 
                                FROM information_schema.columns 
                                WHERE table_name = 'companies' AND column_name = 'logo_url'
                            """)
                            logo_url_exists = cursor.fetchone() is not None
                            print(f"üîç PostgreSQL - Logo URL kolonu mevcut mu: {logo_url_exists}")
                        except Exception as pg_error:
                            print(f"‚ö†Ô∏è PostgreSQL kolon kontrol hatasƒ±: {pg_error}")
                            logo_url_exists = False
                    else:
                        # SQLite i√ßin
                        try:
                            cursor.execute("PRAGMA table_info(companies)")
                            columns = cursor.fetchall()
                            logo_url_exists = any(col[1] == 'logo_url' for col in columns)
                            print(f"üîç SQLite - Logo URL kolonu mevcut mu: {logo_url_exists}")
                        except Exception as sqlite_error:
                            print(f"‚ö†Ô∏è SQLite kolon kontrol hatasƒ±: {sqlite_error}")
                            logo_url_exists = False
                    
                    if not logo_url_exists:
                        print(f"üîç Logo URL kolonu ekleniyor...")
                        try:
                            if hasattr(self.db, 'db_adapter') and self.db.db_adapter.db_type == 'postgresql':
                                # PostgreSQL
                                cursor.execute("ALTER TABLE companies ADD COLUMN logo_url TEXT")
                            else:
                                # SQLite
                                cursor.execute("ALTER TABLE companies ADD COLUMN logo_url TEXT")
                            conn.commit()
                            print(f"‚úÖ Logo URL kolonu eklendi!")
                        except Exception as add_col_error:
                            print(f"‚ö†Ô∏è Logo URL kolonu eklenirken hata: {add_col_error}")
                    
                    if hasattr(self.db, 'get_company_info'):
                        # Database adapter kullan
                        print(f"üîç Database adapter kullanƒ±lƒ±yor...")
                        company_info = self.db.get_company_info(company_id)
                        print(f"üîç Company info: {company_info}")
                        if company_info and company_info.get('logo_url'):
                            current_logo_url = company_info['logo_url']
                            # Mevcut logo URL'i koru
                            update_fields.append(f"logo_url = {placeholder}")
                            update_values.append(current_logo_url)
                            print(f"üîç Logo URL korunuyor (DB Adapter): {current_logo_url}")
                        else:
                            print(f"‚ö†Ô∏è Company info veya logo_url bulunamadƒ±: {company_info}")
                    else:
                        # Fallback: Eski y√∂ntem
                        print(f"üîç Fallback y√∂ntem kullanƒ±lƒ±yor...")
                        cursor.execute(f'''
                            SELECT logo_url FROM companies WHERE company_id = {placeholder}
                        ''', (company_id,))
                        current_logo_result = cursor.fetchone()
                        print(f"üîç Logo query result: {current_logo_result}")
                        
                        if current_logo_result:
                            current_logo_url = current_logo_result[0] if hasattr(current_logo_result, '__getitem__') else current_logo_result.get('logo_url', '')
                            if current_logo_url:
                                # Mevcut logo URL'i koru
                                update_fields.append(f"logo_url = {placeholder}")
                                update_values.append(current_logo_url)
                                print(f"üîç Logo URL korunuyor (Fallback): {current_logo_url}")
                            else:
                                print(f"‚ö†Ô∏è Logo URL bo≈ü: {current_logo_url}")
                        else:
                            print(f"‚ö†Ô∏è Logo query sonucu bulunamadƒ±")
                except Exception as logo_error:
                    print(f"‚ö†Ô∏è Logo URL koruma hatasƒ±: {logo_error}")
                    import traceback
                    traceback.print_exc()
                
                # updated_at her zaman ekle
                update_fields.append(f"updated_at = {timestamp_func}")
                
                # Eƒüer g√ºncellenecek alan varsa UPDATE yap
                print(f"üîç Update fields: {update_fields}")
                print(f"üîç Update values: {update_values}")
                if update_fields:
                    update_values.append(company_id)  # WHERE clause i√ßin
                    update_sql = f"""
                        UPDATE companies 
                        SET {', '.join(update_fields)}
                    WHERE company_id = {placeholder}
                    """
                    print(f"üîç Update SQL: {update_sql}")
                    cursor.execute(update_sql, update_values)
                    print(f"üîç Update ba≈üarƒ±lƒ±!")
                
                # Kullanƒ±cƒ± bilgilerini g√ºncelle - Sadece email dolu ise
                if data.get('email') and data.get('email').strip():
                    cursor.execute(f"""
                            UPDATE users 
                        SET email = {placeholder}
                        WHERE company_id = {placeholder}
                        """, (data.get('email').strip(), company_id))
                
                conn.commit()
                conn.close()
                
                print(f"‚úÖ Profile updated successfully for company: {company_id}")
                return jsonify({'success': True, 'message': 'Profil ba≈üarƒ±yla g√ºncellendi'})
                    
            except Exception as e:
                print(f"‚ùå Profile update error: {str(e)}")
                import traceback
                traceback.print_exc()
                return jsonify({'success': False, 'error': f'Sunucu hatasƒ±: {str(e)}'}), 500

        @self.app.route('/api/company/<company_id>/profile/upload-logo', methods=['POST'])
        def upload_company_logo(company_id):
            """≈ûirket logosu y√ºkle"""
            try:
                # Session kontrol√º
                user_data = self.validate_session()
                if not user_data or user_data.get('company_id') != company_id:
                    return jsonify({'success': False, 'error': 'Ge√ßersiz oturum'}), 401
                
                if 'logo' not in request.files:
                    return jsonify({'success': False, 'error': 'Dosya se√ßilmedi'}), 400
                
                file = request.files['logo']
                if file.filename == '':
                    return jsonify({'success': False, 'error': 'Dosya se√ßilmedi'}), 400
                
                # Dosya uzantƒ±sƒ±nƒ± kontrol et
                allowed_extensions = {'png', 'jpg', 'jpeg', 'gif'}
                file_extension = file.filename.rsplit('.', 1)[1].lower()
                
                if file_extension not in allowed_extensions:
                    return jsonify({'success': False, 'error': 'Ge√ßersiz dosya formatƒ±. PNG, JPG, JPEG, GIF desteklenir.'}), 400
                
                # Dosya boyutunu kontrol et (max 5MB)
                file.seek(0, 2)  # Dosya sonuna git
                file_size = file.tell()
                file.seek(0)  # Ba≈üa d√∂n
                
                if file_size > 5 * 1024 * 1024:  # 5MB
                    return jsonify({'success': False, 'error': 'Dosya boyutu 5MB\'dan b√ºy√ºk olamaz'}), 400
                
                # Dosya adƒ±nƒ± olu≈ütur
                import uuid
                filename = f"logo_{company_id}_{uuid.uuid4().hex[:8]}.{file_extension}"
                
                # Upload klas√∂r√ºn√º olu≈ütur
                upload_folder = os.path.join(os.getcwd(), 'static', 'uploads', 'logos')
                os.makedirs(upload_folder, exist_ok=True)
                
                # Dosyayƒ± kaydet
                file_path = os.path.join(upload_folder, filename)
                file.save(file_path)
                
                # Veritabanƒ±nda logo URL'ini g√ºncelle - Database Adapter kullan
                logo_url = f'/static/uploads/logos/{filename}'
                print(f"üîç Logo URL olu≈üturuldu: {logo_url}")
                
                print(f"üîç Database objesi t√ºr√º: {type(self.db)}")
                print(f"üîç Database objesi: {self.db}")
                print(f"üîç update_company_logo_url mevcut mu: {hasattr(self.db, 'update_company_logo_url')}")
                
                if hasattr(self.db, 'update_company_logo_url'):
                    # Database adapter'da update_company_logo_url fonksiyonu varsa kullan
                    print(f"üîç Database adapter ile logo URL g√ºncelleniyor...")
                    success = self.db.update_company_logo_url(company_id, logo_url)
                    print(f"üîç Logo URL g√ºncelleme sonucu: {success}")
                    if not success:
                        print(f"‚ùå Database'de logo URL g√ºncellenemedi: {company_id}")
                        return jsonify({'success': False, 'error': 'Logo URL veritabanƒ±nda g√ºncellenemedi'}), 500
                    else:
                        print(f"‚úÖ Logo URL ba≈üarƒ±yla g√ºncellendi: {company_id} -> {logo_url}")
                else:
                    print(f"‚ö†Ô∏è Database adapter'da update_company_logo_url fonksiyonu bulunamadƒ±!")
                    print(f"üîç Mevcut database metodlarƒ±: {[attr for attr in dir(self.db) if not attr.startswith('_')]}")
                    
                    # Fallback: Eski y√∂ntem
                    conn = self.db.get_connection()
                    cursor = conn.cursor()
                    
                    placeholder = self.db.get_placeholder() if hasattr(self.db, 'get_placeholder') else '?'
                    
                    # Timestamp fonksiyonu
                    if hasattr(self.db, 'db_adapter') and self.db.db_adapter.db_type == 'postgresql':
                        timestamp_func = 'CURRENT_TIMESTAMP'
                    else:
                        timestamp_func = 'datetime(\'now\')'
                    
                    # logo_url kolonunu g√ºncelle
                    try:
                        cursor.execute(f"""
                            UPDATE companies 
                            SET logo_url = {placeholder}, updated_at = {timestamp_func}
                            WHERE company_id = {placeholder}
                        """, (logo_url, company_id))
                        
                        conn.commit()
                        conn.close()
                    except Exception as db_error:
                        # logo_url kolonu yoksa, sadece updated_at g√ºncelle
                        if 'logo_url' in str(db_error) and 'does not exist' in str(db_error):
                            print(f"‚ö†Ô∏è logo_url kolonu bulunamadƒ±, sadece updated_at g√ºncelleniyor")
                            cursor.execute(f"""
                                UPDATE companies 
                                SET updated_at = {timestamp_func}
                                WHERE company_id = {placeholder}
                            """, (company_id,))
                            conn.commit()
                            conn.close()
                        else:
                            raise db_error
                
                return jsonify({
                    'success': True, 
                    'message': 'Logo ba≈üarƒ±yla y√ºklendi',
                    'logo_url': logo_url
                })
                    
            except Exception as e:
                print(f"‚ùå Logo upload error: {str(e)}")
                import traceback
                traceback.print_exc()
                return jsonify({'success': False, 'error': f'Y√ºkleme hatasƒ±: {str(e)}'}), 500
        
        @self.app.route('/api/company/<company_id>/change-password', methods=['PUT'])
        def change_company_password(company_id):
            """≈ûirket ≈üifresini deƒüi≈ütir"""
            try:
                # Session kontrol√º
                user_data = self.validate_session()
                if not user_data or user_data.get('company_id') != company_id:
                    return jsonify({'success': False, 'error': 'Ge√ßersiz oturum'}), 401
                
                data = request.get_json()
                if not data or not all(k in data for k in ['current_password', 'new_password']):
                    return jsonify({'success': False, 'error': 'Mevcut ve yeni ≈üifre gerekli'}), 400
                
                # Mevcut ≈üifre kontrol√º
                conn = self.db.get_connection()
                cursor = conn.cursor()
                
                placeholder = self.db.get_placeholder() if hasattr(self.db, 'get_placeholder') else '?'
                cursor.execute(f'SELECT password_hash FROM users WHERE company_id = {placeholder} AND role = \'admin\'', (company_id,))
                stored_password = cursor.fetchone()
                
                if not stored_password or not bcrypt.checkpw(data['current_password'].encode('utf-8'), stored_password[0].encode('utf-8')):
                    return jsonify({'success': False, 'error': 'Mevcut ≈üifre yanlƒ±≈ü'}), 401
                
                # Yeni ≈üifre validation
                is_valid, validation_errors = self.validate_password_strength(data['new_password'])
                if not is_valid:
                    return jsonify({
                        'success': False, 
                        'error': '≈ûifre gereksinimleri kar≈üƒ±lanmƒ±yor',
                        'validation_errors': validation_errors
                    }), 400
                
                # Yeni ≈üifre hash'le
                new_password_hash = bcrypt.hashpw(data['new_password'].encode('utf-8'), bcrypt.gensalt()).decode('utf-8')
                
                # ≈ûifre g√ºncelle
                cursor.execute(f"""
                    UPDATE users 
                    SET password_hash = {placeholder} 
                    WHERE company_id = {placeholder}
                """, (new_password_hash, company_id))
                
                conn.commit()
                conn.close()
                
                return jsonify({'success': True, 'message': '≈ûifre ba≈üarƒ±yla deƒüi≈ütirildi'})
                    
            except Exception as e:
                return jsonify({'success': False, 'error': str(e)}), 500

        @self.app.route('/api/company/<company_id>/delete-account', methods=['POST'])
        def company_delete_account(company_id):
            """≈ûirket hesabƒ±nƒ± sil - Self Service"""
            user_data = self.validate_session()
            if not user_data or user_data['company_id'] != company_id:
                return jsonify({'error': 'Yetkisiz eri≈üim'}), 401
            
            try:
                data = request.json
                password = data.get('password')
                
                if not password:
                    return jsonify({'success': False, 'error': '≈ûifre gerekli'}), 400
                
                # ≈ûifre kontrol√º
                conn = self.db.get_connection()
                cursor = conn.cursor()
                
                placeholder = self.db.get_placeholder() if hasattr(self.db, 'get_placeholder') else '?'
                cursor.execute(f'SELECT password_hash FROM users WHERE company_id = {placeholder} AND role = \'admin\'', (company_id,))
                stored_password = cursor.fetchone()
                
                if not stored_password or not bcrypt.checkpw(password.encode('utf-8'), stored_password[0].encode('utf-8')):
                    return jsonify({'success': False, 'error': 'Yanlƒ±≈ü ≈üifre'}), 401
                
                # Hesap silme i≈ülemi
                tables_to_clean = ['detections', 'violations', 'cameras', 'users', 'sessions', 'companies']
                
                for table in tables_to_clean:
                    cursor.execute(f'DELETE FROM {table} WHERE company_id = {placeholder}', (company_id,))
                
                conn.commit()
                conn.close()
                
                # Oturumu temizle
                session.clear()
                
                return jsonify({'success': True, 'message': 'Hesabƒ±nƒ±z ba≈üarƒ±yla silindi'})
                
            except Exception as e:
                return jsonify({'success': False, 'error': str(e)}), 500
        
