        @self.app.route('/api/company/<company_id>/cameras/<camera_id>/toggle', methods=['POST'])
        def toggle_camera_status(company_id, camera_id):
            """Kamera durumunu aktif/pasif yap"""
            try:
                user_data = self.validate_session()
                if not user_data or user_data['company_id'] != company_id:
                    return jsonify({'success': False, 'error': 'Unauthorized'}), 401
                
                logger.info(f"üîÑ Toggle request for camera: {camera_id}, company: {company_id}")
                
                # Kamerayƒ± veritabanƒ±ndan al
                camera = self.db.get_camera_by_id(camera_id, company_id)
                if not camera:
                    logger.error(f"‚ùå Camera not found: {camera_id} for company: {company_id}")
                    return jsonify({'success': False, 'error': 'Kamera bulunamadƒ±'}), 404
                
                logger.info(f"üìπ Current camera status: {camera.get('status', 'unknown')}")
                
                # Yeni durumu belirle
                current_status = camera.get('status', 'active')
                new_status = 'inactive' if current_status == 'active' else 'active'
                
                logger.info(f"üîÑ Toggling camera status from '{current_status}' to '{new_status}'")
                
                # Kamerayƒ± g√ºncelle
                success = self.db.update_camera_status(camera_id, company_id, new_status)
                
                if success:
                    logger.info(f"‚úÖ Camera status updated successfully to: {new_status}")
                    return jsonify({
                        'success': True,
                        'message': f'Kamera durumu {new_status} olarak g√ºncellendi',
                        'new_status': new_status
                    })
                else:
                    logger.error(f"‚ùå Failed to update camera status to: {new_status}")
                    return jsonify({'success': False, 'error': 'Kamera durumu g√ºncellenemedi'}), 500
                
            except Exception as e:
                logger.error(f"Camera toggle error: {e}")
                import traceback
                logger.error(f"Traceback: {traceback.format_exc()}")
                return jsonify({'success': False, 'error': str(e)}), 500
        
