    def add_health_check(self):
        """ƒ∞Yƒ∞LE≈ûTƒ∞Rƒ∞LDƒ∞: Enhanced health check endpoint"""
        @self.app.route('/health', methods=['GET'])
        def health_check():
            """Enhanced health check endpoint for monitoring"""
            try:
                # Check database connection (skip in production for faster response)
                db_status = "healthy"
                if not os.environ.get('RENDER'):
                    try:
                        conn = self.db.get_connection()
                        cursor = conn.cursor()
                        cursor.execute("SELECT 1")
                        conn.close()
                    except Exception as e:
                        db_status = f"unhealthy: {str(e)}"
                else:
                    # In production, just return healthy to avoid slow health checks
                    db_status = "healthy"
                
                # Check application status
                app_status = "healthy"
                
                # Overall health
                healthy = db_status == "healthy" and app_status == "healthy"
                
                response = {
                    "status": "healthy" if healthy else "unhealthy",
                    "timestamp": datetime.now().isoformat(),
                    "version": "2.0.0",
                    "services": {
                        "database": db_status,
                        "application": app_status,
                        "cache": "healthy",
                        "rate_limiting": "active"
                    },
                    "uptime": "running",
                    "features": {
                        "caching": True,
                        "mobile_optimization": True,
                        "export_functionality": True,
                        "enhanced_error_handling": True
                    }
                }
                
                return jsonify(response), 200 if healthy else 503
                
            except Exception as e:
                logger.error(f"Health check failed: {e}")
                return jsonify({
                    "status": "unhealthy",
                    "error": str(e),
                    "timestamp": datetime.now().isoformat()
                }), 503
        
